import React, { useEffect, useState } from "react";
import { useAppSelector } from "../../hooks/redux";
import { useParams } from "react-router";
import { vacancy } from "../../types/vacancy";
import { Layout, Image, Rate, Button } from "antd";
import "./VacancyCard.css";
const VacancyCard = () => {
  const { Content } = Layout;
  const [cuurentVacancy, setCurrentVacncy] = useState<vacancy>({
    _id: "",
    benefits: [],
    company: "",
    date: "",
    direction: "",
    experience: "",
    graid: "",
    location: "",
    logo: "",
    price: 0,
    qualifications: [],
    responsibilities: [],
    about: "",
    title: "",
    testId: "",
    aboutCompany: "",
    occupied: "",
    repliesUsers: [],
  });
  const { id } = useParams();
  let currentVacancy: vacancy | undefined;
  useEffect(() => {
    fetch(`http://158.160.28.109/api/vacancies/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setCurrentVacncy({ ...currentVacancy, ...data });
      });
  }, [id]);

  return (
    <div className="vacancy-card">
      {cuurentVacancy ? (
        <Content>
          {/* <img src={cuurentVacancy.logo} alt={cuurentVacancy.company} /> */}
          <div className="vacancy-card__info-content">
            <Image
              className={"vacancy__company-logo"}
              width={200}
              height={200}
              src="error"
              fallback="data:image/png;base64,"
            />
            <div className="company">
              <p className="vacancy-card__company">{cuurentVacancy.company}</p>
              <Rate defaultValue={2} disabled />
              <div style={{ marginTop: "25px" }}>
                <Button style={{ width: "200px" }}>Откликнуться</Button>
              </div>
            </div>
          </div>

          <div className="vacancy-card__info">
            <h2 className="vacancy-card__list vacancy__title">
              {cuurentVacancy.title}
            </h2>
            <div className="vacancy-card__main-Info">
              {/* <p className="vacancy-card__list experience">
                {cuurentVacancy.experience}
              </p> */}
              <p className=" vacancy-card__list schedule">
                {`График: ${cuurentVacancy.occupied}`}
              </p>
              <p className="vacancy-card__list location">
                {`Город: ${cuurentVacancy.location}`}
              </p>
              <p className="vacancy-card__list price">{`От: ${cuurentVacancy.price} ₽`}</p>
            </div>

            <p className="vacancy-card__list subtitle">
              <span>{"Описание:"}</span>
              <hr />
              {cuurentVacancy.about}
            </p>

            <ul className="vacancy-card__list responsibilities">
              <p className="">{`Обязанности:`}</p>
              <hr />
              {cuurentVacancy.responsibilities.map((responsibility, index) => (
                <li key={index} className="vacancy-card__lis">
                  {responsibility}
                </li>
              ))}
            </ul>
            <ul className="vacancy-card__list qualifications">
              <hr />
              <p>{`Требования:`}</p>
              {cuurentVacancy.qualifications.map((qualification, index) => (
                <li key={index} className="vacancy-card__lis">
                  {qualification}
                </li>
              ))}
            </ul>
            <ul className="vacancy-card__list benefits">
              <p>{`Мы Предлагаем:`}</p>
              <hr />
              {cuurentVacancy.benefits.map((benefit, index) => (
                <li key={index} className="vacancy-card__lis">
                  {benefit}
                </li>
              ))}
            </ul>
          </div>
        </Content>
      ) : (
        <h1>нету</h1>
      )}
    </div>
  );
};

export default VacancyCard;
